<style lang="less">
  @import "./style/index.less";
</style>

<template>

  <div class="alert {{alertVariant}}{{modifiers}}">
    <div class="alert_content">
      <slot></slot>
    </div>
  </div>

</template>

<script>

  var elements = require( 'elements' );

  module.exports = {
    props: [ 'type', 'content', 'closable', 'icon', 'button' ],
    computed: {
      alertVariant: function() {
        return 'alert-' + this.type;
      },
      modifiers: function() {
        var
            closable = this.closable ? ' alert-closable ' : '',
            hasIcon = this.icon ? ' alert-hasIcon ' : '',
            hasButton = this.button ? ' alert-hasButton ' : '';

        return closable + hasIcon + hasButton;
      }
    }

  }

</script>
