"use strict";
var
  MessageFormat = require('message-format'),
  compile = function(msg) {
    return new MessageFormat('de-CH', msg);
  };
  
module.exports = {
  id: 'de-ch',
  LOCALIZATION_EXAMPLE_SIMPLE: compile('Ein einfacher Text'),
  LOCALIZATION_EXAMPLE_PLACEHOLDER: compile('Benutzer {username} hat {bidPrice} CHF geboten'),
  LOCALIZATION_EXAMPLE_PLURALIZATION: compile(
    `{ numBananas, plural,
      =0 {Keine Bananen} 
      one {Eine Banane}
      =10 {Alle Bananen}
      other {# Bananen} }`),
  LOCALIZATION_EXAMPLE_CONTEXT: compile(
    `{ gender, select,
      male {Er hat}
      female {Sie hat}
      androgyne {Es hat}
      other {Sie haben} } das Spiel gewonnen.`)
};
