"use strict";
var
  MessageFormat = require('message-format'),
  compile = function(msg) {
    return new MessageFormat('fr-CH', msg);
  };
  
  
module.exports = {
  id: 'fr-ch',
  LOCALIZATION_EXAMPLE_SIMPLE: compile('Un texte simple'),
  LOCALIZATION_EXAMPLE_PLACEHOLDER: compile('Utilisateur {username} a offrit {bidPrice} CHF'),
  LOCALIZATION_EXAMPLE_PLURALIZATION: compile(
    `{ numBananas, plural,
      =0 {Pas des bananes} 
      one {Une banane}
      =10 {Toutes les bananes}
      other {# Bananes} }`),
  LOCALIZATION_EXAMPLE_CONTEXT: compile(
    `{ gender, select,
      male {Il a }
      female {Elle a }
      androgyne {Il a}
      other {Elles ont } } gagn√© le jeu.`)
};