"use strict";

var
  locals = require('vuex_localization').locals,
  getters = {};
 
for ( var entryKey in locals.default ) {
  if ( locals.default.hasOwnProperty( entryKey ) ) {
    (function (entryKey) {
      getters[entryKey] = function(state) {
        var potentialResult = state.localization.locals[state.route.params.local][entryKey];
        if (typeof potentialResult !== 'undefined' && typeof potentialResult.format === 'function' )  {
          return state.localization.locals[state.route.params.local][entryKey];
        }
        potentialResult = state.localization.locals['default'][entryKey];
        if (typeof potentialResult !== 'undefined' && typeof potentialResult.format === 'function'){
          return state.localization.locals['default'][entryKey];
        } else {
          return ''
        }
    
      };
    })(entryKey);
    
  }
}

module.exports = {
  vuex: {
    getters
  }
};