"use strict";

var
  VueRouter = require('vue-router'),
  Vue = require('vue');

Vue.use(VueRouter);

var router = new VueRouter();

router.map( {
  '/:local/': {
    name: 'home',
    component: require( 'page~home' )
  },
  '/:local/icon-page': {
    component: require( 'page~icons' )
  },
  '/:local/text-page': {
    component: require( 'page~typography' )
  },
  '/:local/form-page': {
    component: require( 'page_form-elements' )
  },
  '/:local/user-profile': {
    name: 'user-profile',
    component: { template: '<router-view></router-view>' },
    subRoutes: {
      '/': {
        component: require('page_user-profile'),
        subRoutes: {
          'verify-email/:token': {
            name: 'verify-email-token',
            component: require('sub_verify-email')
          },
          'verify-email-protected/:token': {
            name: 'verify-email-protected',
            component: require('sub_verify-email-protected')
          }
        }
      }
    }
  },
  '/:local/forgot-password': {
    name: 'forgot-password',
    component: require('page~forgot-password'),
    subRoutes: {
      'account-recovery': {
        name: 'account-recovery',
        component: require('sub~account-recovery')
      },
      'account-recreate': {
        name: 'account-recreate',
        component: require('sub~account-recreate')
      },
      '/': {
        component: require('sub~forgot-password')
      }
    }
  },
  '/:local/localization': {
    component: require('page_localization')
  },
  '/:local/panel': {
    component: require('page_panel')
  }
} );

module.exports = router;
