"use strict";

module.exports = {
  handleErrorCase: function(ctx, response) {
    if (response.status === 0) {
      ctx.setError('Es konnte leider keine Verbindung zum Server hergestellt werden. Stellen Sie eine Internetverbindung her oder versuchen sie es sp√§ter noch einmal.')
    } else {
      ctx.setError(response.data.message);
    }
  }
};