"use strict";

module.exports = {
  methods: {
    handleErrorCase: function(response) {
      if (response.status === 0) {
        this.setError('Es konnte leider keine Verbindung zum Server hergestellt werden. Stellen Sie eine Internetverbindung her oder versuchen sie es sp√§ter noch einmal.')
      } else {
        this.setError(response.data.message);
      }
    }
  }
};