      contextTexts: function() {
        return [
          {id: 1, text: 'LOCALIZATION_EXAMPLE_CONTEXT', params: { gender: 'male'} },
          {id: 2, text: 'LOCALIZATION_EXAMPLE_CONTEXT', params: { gender: 'female'} },
          {id: 3, text: 'LOCALIZATION_EXAMPLE_CONTEXT', params: { gender: 'androgyne'} },
          {id: 4, text: 'LOCALIZATION_EXAMPLE_CONTEXT', params: { gender: 'mixed'} }
