<style lang="less">

</style>

<template>
  <r-panel title="Personal information" type="editable">
    <button class="btn btn-secondary" slot="editable" @click="toggleEditMode('personalData')" v-show="!states.personalData.isEditMode">Edit</button>
    <div>
      <form novalidate>
        <r-form-item
            label="Salutation"
            input-id="radio"
            type="radio"
            value="Mr"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Salutation"
            input-id="radio"
            type="radio"
            placeholder="Mrs"
            value="Mrs"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Firstname"
            input-id="firstname"
            type="text"
            placeholder="Firstname"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Lastname"
            input-id="lastname"
            type="text"
            placeholder="Lastname"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Username"
            input-id="username"
            type="text"
            placeholder="Username"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Birthdate"
            input-id="birthdate"
            type="text"
            placeholder="Birthdate"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Language"
            input-id="language"
            type="radio"
            value="German"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Language"
            input-id="language"
            type="radio"
            value="French"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <r-form-item
            label="Language"
            input-id="language"
            type="radio"
            value="English"
            :readonly="!states.personalData.isEditMode"
        ></r-form-item>
        <template v-if="states.personalData.isEditMode">
          <button class="btn btn-cancel" @click.prevent="abort('personalData')">Abort</button>
          <button class="btn btn-primary" @click.prevent="confirm('personalData')">Confirm</button>
        </template>
      </form>
    </div>
  </r-panel>
  <r-panel title="E-Mail-Address" type="editable"></r-panel>
  <r-panel title="Password" type="editable"></r-panel>
  <r-panel title="Ratings"></r-panel>
</template>

<script>
  var panel = require('ricardo-component_panel'),
      formItem = require('ricardo-element_form-elements').components.formItem;

  module.exports = {
    components: {
      'r-panel': panel,
      'r-form-item': formItem
    },
    data: function() {
      return {
        states: {
          personalData: {
            isEditMode: false
          },
          emailData: {
            isEditMode: false
          },
          passwordData: {
            isEditMode: false
          }
        }
      }
    },
    methods: {
      toggleEditMode: function(area) {
        this.states[area].isEditMode = !this.states[area].isEditMode;
      },
      abort: function(area) {
        this.toggleEditMode(area);
      },
      confirm: function(area) {
        // do some fancy stuff like serializing the form
        this.toggleEditMode(area);
      }
    }
  };
</script>
