      validation-message="['Please enter an email address', 'Please enter a valid email address.' ]"
  ></r-form-item>
  <r-form-item
      variant="email"
      label="Email readonly"
      input-id="email-readonly"
      type="email"
      placeholder="enter an email address..."
      validation-type="['required', 'email' ]"
      readonly="true"
      default-value="Readonly input"
      validation-message="['Please enter an email address', 'Please enter a valid email address.' ]"
  ></r-form-item>
  <r-form-item
      input-id="min-validation"
