module.exports = {
  install: function (Vue, options) {
    var store = options.store;

    Vue.prototype.$t = function(message, params = null, lang = 'default') {
      return store.getters.locals[store.state.route.params.local] ? store.getters.locals[store.state.route.params.local][message].format(params) : message;
    }
  }
};
